questionvariables.text:
x0 : -2;
f0 : 1;
x1 : 0;
f1 : sin(x)
/* Note, we should really use inf instead of 1000 here, but Maxima gives up plotting with the options used by STACK */
pg:if x<x0 then f0 else if x<x1 then 1000 else f1;
CS:matrix([1,x0,x0^2,x0^3],[0,1,2*x0,3*x0^2],[1,x1,x1^2,x1^3],[0,1,2*x1,3*x1^2]);
CT:transpose(matrix([ev(f0,x=x0),subst(x=x0,diff(f0,x)),ev(f1,x=x1),subst(x=x1,diff(f1,x))]));
CV:transpose(matrix([a0,a1,a2,a3]));
CS2:invert(CS).CT;
ta : matrix([1,x,x^2,x^3]).CS2;
tapt : if (x>x0 and x<x1) then ta else 1000;

a:rand(4);
b:a+1+rand(4);
c:b+1+rand(4);
d:c+1+rand(4);
A:matrix([a,b],[c,d]);
B:matrix([a,b],[c,k]);
ta:d;
AB:A.B;
BA:B.A;

slope:rand([1, 2, 3, 1/2]);
x0:rand_with_step(1, 3, 1);
y0:rand_with_step(1, 3, 1);
a0:rand_with_step(-3, -1, 1);
c0:rand_with_step(1, 3, 1);
b0:(slope-a0*x0^2-c0)/x0;
b1:decimalplaces(b0, 3);
dumvar74:f(x) := a0*x^2+b0*x+c0;
dumvar75:g(x) := int(f(x), x);
g3:simplify(g(x));
g2:subst(x0, x, g(x));
d1:y0-g2;
d0:decimalplaces(d1, 3);
slopeeqn:y - y0 = slope*(x-x0);
y2:-x0*slope+y0;
slopeeqn2:y = slope*x+y2;
slopefn:slope*x+y2;

A:ev(rand(matrix([5,5],[5,5]))+matrix([2,2],[2,2]),simp);
 B:ev(rand(matrix([5,5],[5,5]))+matrix([2,2],[2,2]),simp);
 TA:ev(A.B,simp);
 TB:ev(A*B,simp);
 BT:transpose(B);
 C:zeromatrix (first(matrix_size(A)), second(matrix_size(A)));
 S:for a:1 thru first(matrix_size(A)) do for b:1 thru second(matrix_size(A)) do C[ev(a,simp),ev(b,simp)]:apply("+",zip_with("*",A[ev(a,simp)],BT[ev(b,simp)]));
 D:ev(C,simp);

feedbackvariables.text:
