 l:setify(listofvars(ans1));
 l:setdifference(l,set(t));
 l:listify(l);
 lv:length(l);
 b1:ev(ans1,t=0,fullratsimp);
 b2:ev(ans1,t=1,fullratsimp);
 m:if not(b2=0) then fullratsimp(b1/b2) else 0;
 m:float(m);

